    /**
     * 获取指定图标的 SVG 代码
     * @param string $name 图标名称
     * @return string SVG 代码或空字符串
     */
    public static function get($name) {
        if (isset(self::$icons[$name])) {
            return '<span class="cosmdl-icon cosmdl-icon-' . esc_attr($name) . '" style="display:inline-block;width:1.2em;height:1.2em;vertical-align:-0.2em;margin-right:6px;fill:currentColor;">' . self::$icons[$name] . '</span>';
        }
        return '';
    }
}

